/**
 * Searches for relevant articles/sections within a given text based on keywords
 */
const searchInText = (
  query: string,
  text: string,
  separator: string = "---"
): string[] => {
  const queryWords = query
    .toLowerCase()
    .split(/\s+/)
    .filter((w) => w.length > 3);
  if (queryWords.length === 0 || !text) return [];

  const articles = text.split(separator).filter((art) => art.trim() !== "");

  const relevantArticles = articles.filter((article) => {
    const articleLower = article.toLowerCase();
    return queryWords.some((word) => articleLower.includes(word));
  });

  return relevantArticles;
};

/**
 * Finds relevant content from both the VAT Act and its Regulations
 */
export const findRelevantContent = (
  keywords: string[]
): ContextRetrievalResult => {
  console.log("🔍 [Context Retrieval] Starting...");
  console.log("📋 [Search Keywords]", { keywords, count: keywords.length });

  if (keywords.length === 0) {
    console.log(
      "⚠️  [Context Retrieval] No keywords provided - returning empty context"
    );
    return {
      actContext: "",
      regulationsContext: "",
    };
  }

  const query = keywords.join(" ");

  const relevantActArticles = searchInText(query, VAT_ACT_TEXT);
  const relevantRegulationsArticles = searchInText(query, VAT_REGULATIONS_TEXT);

  console.log("✅ [Context Retrieval] Found articles:", {
    ЗДДС: relevantActArticles.length,
    ППЗДДС: relevantRegulationsArticles.length,
  });

  let actContext = "";
  if (relevantActArticles.length > 0) {
    const contextHeader =
      "Предоставени са следните членове от ЗДДС като контекст:";
    actContext = `${contextHeader}\n\n${relevantActArticles.join("\n---\n")}`;
  }

  let regulationsContext = "";
  if (relevantRegulationsArticles.length > 0) {
    const contextHeader =
      "Предоставени са следните членове от ППЗДДС като контекст:";
    regulationsContext = `${contextHeader}\n\n${relevantRegulationsArticles.join(
      "\n---\n"
    )}`;
  }

  console.log("📊 [Context Size]", {
    actContext: actContext,
    regulationsContext: regulationsContext,
  });

  return { actContext, regulationsContext };
};

class VATContentService {
  async findRelevantContent(
    keywords: string[]
  ): Promise<ContextRetrievalResult> {
    // Use the synchronous in-memory search
    return findRelevantContent(keywords);
  }
}

export const vatContentService = new VATContentService();
